const hourHand=document.getElementById("hour"),minuteHand=document.getElementById("minute"),secondHand=document.getElementById("second");function updateClock(){const e=new Date,t=e.getHours(),n=e.getMinutes(),a=e.getSeconds(),o=30*(t%12)+.5*n,d=6*n,c=6*a;hourHand.style.transform=`translate(-50%, 0) rotate(${o}deg)`,minuteHand.style.transform=`translate(-50%, 0) rotate(${d}deg)`,secondHand.style.transform=`translate(-50%, 0) rotate(${c}deg)`}setInterval(updateClock,1e3),updateClock();const infoSection=document.querySelector(".info-section"),observer=new IntersectionObserver((e=>{e.forEach((e=>{e.isIntersecting?infoSection.classList.add("visible"):infoSection.classList.remove("visible")}))}),{threshold:.3});observer.observe(infoSection);
